// src/ReframeForm.js
import React, { useState } from 'react';
import { getReframe } from './api';

function ReframeForm() {
  const [situation, setSituation] = useState('');
  const [loudestThought, setLoudestThought] = useState('');
  const [wishToHear, setWishToHear] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const result = await getReframe(situation, loudestThought, wishToHear);
    setResponse(result);
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '2rem' }}>
      <h2>Micro Reframe Engine</h2>
      <form onSubmit={handleSubmit}>
        <label>What’s happening right now?</label>
        <textarea value={situation} onChange={(e) => setSituation(e.target.value)} rows="3" required />

        <label>What’s the loudest thought in your head?</label>
        <textarea value={loudestThought} onChange={(e) => setLoudestThought(e.target.value)} rows="3" required />

        <label>What do you wish someone would say to you?</label>
        <textarea value={wishToHear} onChange={(e) => setWishToHear(e.target.value)} rows="3" required />

        <button type="submit" disabled={loading} style={{ marginTop: '1rem' }}>
          {loading ? 'Thinking...' : 'Get Reframe'}
        </button>
      </form>

      {response && (
        <div style={{ marginTop: '2rem', backgroundColor: '#f0f0f0', padding: '1rem', borderRadius: '8px' }}>
          <h3>Here’s Your Reframe</h3>
          <pre>{response}</pre>
        </div>
      )}
    </div>
  );
}

export default ReframeForm;
